import React from "react"
import SideLeft from "./SideLeft"
import SideRight from "./SideRight"
import {useParams, useHistory,NavLink} from "react-router-dom"
import {useState} from "react"
import axios from "axios"
const popularVerb = ["be",
    "g",
    "be",
    "eat",
    "have",
    "run",
    "see",
    "do",
    "dance",
    "read",
    "play",
    "write",
    "think",
    "get",
    "say",
    "buy",
    "take",
    "speak",
    "come",
    "work",
    "know",
    "lie",
    "ride",
    "sing",
    "sleep",
    "study",
    "talk",
    "make",
    "teach",
    "want"]

function Index(props) {
    const [PopularVerb] = useState(popularVerb)
    let [dataVrai] = useState("")
    const papa = useParams()
    const [inputValue, setinputValue] = useState("")
    const history = useHistory()
console.log(papa)

function submitSeach(e){
    e.preventDefault()
    e.preventDefault()
     history.push(`/verbs/${inputValue}`)
     console.log(inputValue)
}

function ClickOn(e){
    const result = e.target.textContent

    const options = {
        method: 'GET',
        url: 'https://linguatools-conjugations.p.rapidapi.com/conjugate/',
        params: { verb: result },
        headers: {
            'x-rapidapi-key': '2a50965eebmsh629d096fce14516p1319aejsn22e0ba714d98',
            'x-rapidapi-host': 'linguatools-conjugations.p.rapidapi.com'
        }
    };

    axios.request(options).then(function (response) {
         dataVrai = response.data
        console.log(dataVrai)
    }).catch(function (error) {
        console.error(error);
    });
}


    return (
        <div>
            <div className="jumbotron d-flex justify-content-around">
            <form className="container row " onSubmit={submitSeach}>
              <div className="form-group">
                 <h1 className="text-primary"> Here you can find a verb</h1>
                 <input type="text" className="form-control " value={inputValue} onChange={(e)=>{
                        setinputValue(e.target.value)
                    }}/>
                    <input type="submit" className="btn btn-info mt-4" value="Conjugate" placeholder="Seach for verbs" />

              </div>

            </form>

             </div>

             <div className="row d-flex">

             <SideLeft/>
             <div className="col-sm ml-5 py-5 sideCenter">
                  <h5> Irregular verbs</h5> 
                <hr className="my-4"></hr>
                <ul className="right-ul row">
                    {
                        PopularVerb.map((verb, index) => {
                            return (
                                <>
                                    <li key={index} className="col-sm-2"><NavLink to={"verbs/"+verb} onClick={ClickOn}>{verb}</NavLink> </li>
                                </>
                            )
                        })
                    }
                </ul>
               </div>
             <SideRight/>
             </div>


        </div>
    )
}

export default Index
