import React, { useState } from 'react'
import { useHistory } from 'react-router-dom'
import axios from "axios"
const validator = require("email-validator")

function Contact() {
    const [email, setemail] = useState("")
    const [send, setsend] = useState(false)
    const [text, settext] = useState("")
    const [message, setmessage] = useState("")
    const history = useHistory()
    const [valeurEmail, setValeurEmail] = useState(true)
    let inputEmail = document.getElementById("email")
    inputEmail.addEventListener("blur", function () {
        if (!valeurEmail) {
            setValeurEmail(validator.validate(email))
        }
    })
    const handleClick = (e) => {
        e.preventDefault();
        console.log(email, text)
        setValeurEmail(validator.validate(email))



        /* axios({
            method: 'post',
            url: 'https://backendsimplicyto.herokuapp.com/contact',
            data: {
                email,
                text
            }
        }).then(result => {
            function validateEmail(emailss) {
                const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
                return re.test(String(emailss).toLowerCase());
            }
            const valeurEmail = validateEmail(email)
            if (!valeurEmail) setmessage("The Email have to be an Email")
            else if (text.length < 20) setmessage("The text must contain at least 20 caracters")
            else {
                setsend(true)
            }

        })
            .catch(err => console.log(err)) */
    }

    return (
        <>

            <div className="container my-5 allBody ">
                <form className="mx-5">
                    <div className="form-group row">
                        <label htmlFor="email">Email address</label>
                        <input type="email" className="form-control" id="email" placeholder="name@example.com" name="email" value={email} onChange={(e) => setemail(e.target.value)} />
                        {
                            (!valeurEmail) ? <span className="text-danger">This Email is not valid </span> : <span></span>
                        }
                    </div>


                    <div className="form-group row">
                        <label htmlFor="text-area">Contact us</label>
                        <textarea className="form-control" id="text-area" rows="10" name="text" value={text} onChange={(e) => settext(e.target.value)}></textarea>
                    </div>
                    <div className="form-group row">
                        <button type="button" className="btn btn-info  px-5" data-toggle="modal" data-target="#exampleModal" onClick={handleClick}>Send</button>
                        <span className="text-danger ml-3 mt-2">{message}</span>


                    </div>
                </form>
                {
                    (send) ? (<div>

                        <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                            <div className="modal-dialog">
                                <div className="modal-content">
                                    <div className="modal-header">
                                        <h5 className="modal-title" id="exampleModalLabel">Thank you!</h5>
                                        <button type="button" className="close" data-dismiss="modal" aria-label="Close" onClick={() => history.push("/")}>
                                            <span aria-hidden="true">&times;</span>
                                        </button>
                                    </div>
                                    <div className="modal-body">
                                        <p className="text-info"> Your message has been sent successfully...</p>
                                    </div>
                                    <div className="modal-footer">
                                        <button type="button" className="btn btn-danger" data-dismiss="modal" onClick={() => history.push("/")}>Close</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>) : (<div></div>)
                }

            </div>
        </>
    )
}

export default Contact
