import { useParams, useHistory, NavLink } from "react-router-dom"
import { useState } from "react"
import axios from "axios"
import seulverb from "./seulverb"
import Helmet from "react-helmet"



function SingleVerb() {
    const [inputValue, setinputValue] = useState("")
    const [titre, settitre] = useState("")
    const [valeurTitre, setvaleurTitre] = useState("")
    const [SimplePast1, setSimplePast1] = useState("")
    const [SimplePastValue, setSimplePastValue] = useState("")
    const [PastParticipe, setPastParticipe] = useState("")
    const [PastParticipeValue, setPastParticipeValue] = useState("")
    const [SimplePresent, setSimplePresent] = useState("")
    const [PresentI, setPresentI] = useState("")
    const [PresentIRresult, setPresentIRresult] = useState("")
    const [PresentYou, setPresentYou] = useState("")
    const [PresentYouResult, setPresentYouResult] = useState("")
    const [PresentHe, setPresentHe] = useState("")
    const [PresentHeResult, setPresentHeResult] = useState("")
    const [PresentWe, setPresentWe] = useState("")
    const [PresentWeResult, setPresentWeResult] = useState("")
    const [PresentYou1, setPresentYou1] = useState("")
    const [PresentYou1Result, setPresentYou1Result] = useState("")
    const [PresentThey, setPresentThey] = useState("")
    const [PresentTheyResult, setPresentTheyResult] = useState("")

    const history = useHistory()
    function seachVerb(e) {
        e.preventDefault();
        const options = {
            method: 'GET',
            url: 'https://linguatools-conjugations.p.rapidapi.com/conjugate/',
            params: { verb: inputValue },
            headers: {
                'x-rapidapi-key': '2a50965eebmsh629d096fce14516p1319aejsn22e0ba714d98',
                'x-rapidapi-host': 'linguatools-conjugations.p.rapidapi.com'
            }
        };

        axios.request(options).then(function (response) {
            console.log(response.data);
            settitre(response.data.conjugated_forms[0][0] + " : ")
            setvaleurTitre(response.data.conjugated_forms[0][1])
            setSimplePast1(response.data.conjugated_forms[1][0] + " : ")
            setSimplePastValue(response.data.conjugated_forms[1][1])
            setPastParticipe(response.data.conjugated_forms[2][0] + " : ")
            setPastParticipeValue(response.data.conjugated_forms[2][1])
            setSimplePresent(response.data.conjugation_tables.indicative[0].heading.toUpperCase())
            setPresentI(response.data.conjugation_tables.indicative[0].forms[0][0])
            setPresentIRresult(response.data.conjugation_tables.indicative[0].forms[0][1])
            setPresentYou(response.data.conjugation_tables.indicative[0].forms[1][0])
            setPresentYouResult(response.data.conjugation_tables.indicative[0].forms[1][1])
            setPresentHe(response.data.conjugation_tables.indicative[0].forms[2][0])
            setPresentHeResult(response.data.conjugation_tables.indicative[0].forms[2][1])
            setPresentWe(response.data.conjugation_tables.indicative[0].forms[3][0])
            setPresentWeResult(response.data.conjugation_tables.indicative[0].forms[3][1])
            setPresentYou1(response.data.conjugation_tables.indicative[0].forms[4][0])
            setPresentYou1Result(response.data.conjugation_tables.indicative[0].forms[4][1])
            setPresentThey(response.data.conjugation_tables.indicative[0].forms[5][0])
            setPresentTheyResult(response.data.conjugation_tables.indicative[0].forms[5][1])
        }).catch(function (error) {
            console.error(error);
        });
        history.push(`/conjugation/conjugation-verb-${inputValue}.html`)

    }
    return (
        <div>
            <div className="container my-5">
                <form className="form-inline d-flex justify-content-center" onSubmit={seachVerb} >
                    <div className="form-group mb-2">
                        <input type="text" className="form-control " placeholder="Conjugation" value={inputValue} onChange={(e) => {
                            setinputValue(e.target.value)
                        }} />
                    </div>
                    <input type="submit" value="Conjugation" className="btn btn-info mb-2 ml-2" />
                </form>
            </div>
            <div className="row mt-3  " >
                <div className="col-sm-2 singlever-left bg-info  d-none d-sm-block">
                    Mes publicites seront ici!
               </div>

                <div className="col-sm-8  pb-5">
                    <p>{titre}  <span className="text-danger">{valeurTitre}</span></p>
                    <p>{SimplePast1}  <span className="text-danger">{SimplePastValue}</span></p>
                    <p>{PastParticipe}  <span className="text-danger">{PastParticipeValue}</span></p>

                </div>


                <div className="col-sm-2 singlever-right bg-info d-none d-sm-block">
                    Mes publicites seront ici!
               </div>
            </div>
        </div>
    )
}

export default SingleVerb
