import { BrowserRouter, Switch, Route } from 'react-router-dom';
import CookieConsent, { getCookieConsentValue } from "react-cookie-consent"
import './App.css';
import Header from "./components/Header"
import Index from "./components/Index"
import SingleVerb from "./components/SingleVerb"
import Footer from "./components/Footer"
import Translate from "./components/Translate"
import Contact from "./components/Contact"
import Privacy from "./components/Privacy"
import PageNotfound from "./components/PageNotfound"

function App() {
  const a = document.cookie;
  const b = a.split(";");
  const c = b.map(element => element.split("="))
  console.log(c)

  /* 
  const c = b.split(" ")
  console.log(c.includes("simplecyto")) */
  const myCookie = getCookieConsentValue("simplecyto")
  return (
    <div className="App">
      <BrowserRouter>
        <Header />
        <Switch>
          <Route path="/" exact >
            <Index />
          </Route>
          <Route path="/conjugation" exact >
            <Index />
          </Route>
          <Route path="/privacy-policy" exact >
            <Privacy />
          </Route>
          <Route path="/contact" exact >
            <Contact />
          </Route>
          <Route path="/conjugation/:singleVerb" exact
          >
            <SingleVerb />
          </Route>
          <Route path="/translate/:singleTranslate" exact
          >
            <Translate />
          </Route>
          <Route component={PageNotfound} />
        </Switch>
        <Footer />
        <CookieConsent
          debug={true}
          location={'bottom'}
          style={{ background: "#292b2c", height: "100px", paddingTop: "15px" }}
          buttonStyle={{ background: "#5bc0de", color: "white", margin: "0 105px " }}

          cookieName="simplecyto"
          cookieValue="je suis un camerounais"
          buttonText="I agree"
          expires={365}
          ariaAcceptLabel={true}
        >
          {
            (myCookie !== null) ? (<p>Cookies allow us to provide our services. By using our services, you agree to our use of cookies. See our <a href="/privacy-policy" className="text-warning"> privacy policy </a>for more </p>) : (<p></p>)
          }

        </CookieConsent>
      </BrowserRouter>
    </div>
  );
}

export default App;
