import { useParams, useHistory, NavLink } from "react-router-dom"
import { useState, useEffect } from "react"
import axios from "axios"
import seulverb from "./seulverb"
import Helmet from "react-helmet"
import { map } from "./irregularVerb"

function SingleVerb() {
    const [AllData, setAllData] = useState([])
    const [inputValue, setinputValue] = useState("")
    const { singleVerb } = useParams()
    const history = useHistory()
    console.log("valeur du singleverbe", singleVerb)
    const verbSingleOne = singleVerb.split("-")[2].split(".")[0]
    const title = `Conjugation  ${verbSingleOne} | Conjugate verb ${verbSingleOne} | Simplicyto Conjugator English`
    const description = `Conjugate the English verb ${verbSingleOne}: indicative, past tense, participle, present perfect, passive, conditional, conjugation models and irregular verbs. Translate ride in context, with examples of use  and prononciation.`



    useEffect(() => {

        const options = {
            method: 'GET',
            url: 'https://linguatools-conjugations.p.rapidapi.com/conjugate/',
            params: { verb: verbSingleOne },
            headers: {
                'x-rapidapi-key': '2a50965eebmsh629d096fce14516p1319aejsn22e0ba714d98',
                'x-rapidapi-host': 'linguatools-conjugations.p.rapidapi.com'
            }
        };

        axios.request(options).then(function (response) {
            console.log(response.data);
            setAllData(response.data)
        }).catch(function (error) {
            console.error(error);
        });
    }, [verbSingleOne])

    function seachVerb(e) {
        e.preventDefault()
        history.push(`/conjugation/conjugation-verb-${inputValue}.html`)

    }
    console.log("le voici mon chien", AllData)
    return (
        <div>
            <Helmet>
                <title>{title}</title>
                <meta name="description" content={description} />
            </Helmet>
            <div className="container my-5">
                <form className="form-inline d-flex justify-content-center" onSubmit={seachVerb} >
                    <div className="form-group mb-2">
                        <input type="text" className="form-control " placeholder="Conjugation" value={inputValue} onChange={(e) => {
                            setinputValue(e.target.value)
                        }} />
                    </div>
                    <input type="submit" value="Conjugation" className="btn btn-info mb-2 ml-2" />
                    {/*  {IsLoading && <span className="spinner-border text-primary ml-2" role="status">
                        <span class="sr-only">Loading...</span>
                    </span>} */}
                </form>

            </div>
            <div className="row mt-3  " >
                <div className="col-sm-2 singlever-left bg-info  d-none d-sm-block">
                    Mes publicites seront ici!
               </div>
                <div className="col-sm-8  pb-5">
                    <p>{AllData.conjugated_forms[0][0] + " : "}  <span className="text-danger">{AllData.conjugated_forms[0][1]}</span></p>
                    <p>{AllData.conjugated_forms[1][0] + " : "}  <span className="text-danger">{AllData.conjugated_forms[1][1]}</span></p>
                    <p>{AllData.conjugated_forms[2][0] + " : "}  <span className="text-danger">{AllData.conjugated_forms[2][1]}</span></p>
                    <h4 className="text-info">Indicative</h4>
                    <div className="row">
                        {AllData.conjugation_tables.indicative.map((element, index) => {
                            return (
                                <div className="col-sm" key={index}>

                                    <div className="card m singleverb-card ml-5 mt-5">
                                        <p className="text-center text-danger pt-3">{element.heading}</p>
                                        <ul className="list-group list-group-flush">
                                            <li className="list-group-item"><NavLink to={`/translate/${element.form[0][0]}-${element.form[0][1]}`}> <span className="text-dark"> {element.form[0][0]} </span><span className="text-info">{element.form[0][1]} </span></NavLink>  <i className="fa fa-volume-up ml-4  text-danger" onClick={() => {
                                                const utterance = new SpeechSynthesisUtterance(element.form[0][0] + element.form[0][1])
                                                utterance.rate = .9
                                                utterance.lang = "us-US"
                                                speechSynthesis.speak(utterance)
                                            }}> </i> </li>
                                        </ul>
                                    </div>
                                </div>
                            )

                        })

                        }
                    </div>


                </div>


                <div className="col-sm-2 singlever-right bg-info d-none d-sm-block">
                    Mes publicites seront ici!
               </div>
            </div>
        </div>
    )
}

export default SingleVerb
