import { BrowserRouter, Switch, Route } from 'react-router-dom';
import CookieConsent from "react-cookie-consent"
import './App.css';
import Header from "./components/Header"
import Index from "./components/Index"
import SingleVerb from "./components/SingleVerb"
import Footer from "./components/Footer"
import Translate from "./components/Translate"
import Contact from "./components/Contact"
import Privacy from "./components/Privacy"
import PageNotfound from "./components/PageNotfound"

function App() {
  function getCookie(name) {
    var nameEQ = name + "=";
    var ca = document.cookie.split(';');
    for (var i = 0; i < ca.length; i++) {
      var c = ca[i];
      while (c.charAt(0) === ' ') c = c.substring(1, c.length);
      if (c.indexOf(nameEQ) === 0) return c.substring(nameEQ.length, c.length);
    }
    return null;
  }
  const valeurCookies = getCookie("simplecyto")
  return (
    <div className="App">
      <BrowserRouter>
        <Header />
        <Switch>
          <Route path="/" exact >
            <Index />
          </Route>
          <Route path="/conjugation" exact >
            <Index />
          </Route>
          <Route path="/privacy-policy" exact >
            <Privacy />
          </Route>
          <Route path="/contact" exact >
            <Contact />
          </Route>
          <Route path="/conjugation/:singleVerb" exact
          >
            <SingleVerb />
          </Route>
          <Route path="/translate/:singleTranslate" exact
          >
            <Translate />
          </Route>
          <Route component={PageNotfound} />
        </Switch>
        <Footer />
        {
          (valeurCookies === null) && (<CookieConsent
            debug={true}
            location={'top'}
            style={{ background: "#292b2c", height: "150px", paddingTop: "15px", display: "flex", flexDirection: "row" }}
            buttonStyle={{ background: "#5bc0de", color: "white", margin: "0 105px " }}
            cookieName="simplecyto"
            cookieValue={true}
            buttonText="I agree"
            expires={365}
            ariaAcceptLabel={true}
            overlay
          >
            <p>Cookies allow us to provide our services. {/* By using our services, you agree to our use of cookies. See our */} <a href="/privacy-policy" className="text-warning"> privacy policy </a>for more </p>
          </CookieConsent>)
        }
      </BrowserRouter>
    </div>
  );
}

export default App;
