import React, { useState } from "react";
import { useHistory } from "react-router-dom"

function Index() {
    const [inputValue, setinputValue] = useState("")

    const history = useHistory()


    function submitSeach(e) {
        e.preventDefault()
        history.push(`/conjugation/conjugaison-verb-${inputValue}.html`)
    }

    return (
        <div>
            <div className="allBody row left-landing">
                <div className="col-sm landing1 ">
                    <h2 className="text-info ">SIMPLECYTO</h2>
                    <h5>To find all conjugated forms of a verb, enter a verb in the input field.</h5>
                    <form className="form-inline d-flex justify-content-center mt-4" onSubmit={submitSeach} >
                        <div className="form-group mb-2">
                            <input type="text" className="form-control " placeholder="Conjugation" value={inputValue} onChange={(e) => {
                                setinputValue(e.target.value)
                            }} />
                        </div>
                        <input type="submit" value="Conjugation" className="btn btn-info mb-2 ml-2" />
                    </form>
                </div>
                {/* <div className="col-sm landing ml-5 mr-5 pr-5 d-none d-md-block">

                </div> */}
                <div className="card" >
                    <img src="/landing4.svg" className="card-img-top" alt="..." />
                </div>
            </div>
            <hr className="container"></hr>
            <div className="container my-5">
                <h2 className="text-info">ABOUT US</h2>
                <p>Conjugate English verbs with www.simplecyto.com. All conjugated forms of the most popular verbs. This conjugator generates all English verb forms (present, simple past, imperative, participle,conditional, passive ...) for an infinitive. Simplecyto provides any verb forms of all verbs (auxiliary verbs, strong or weak verbs, regular or irregular verbs, modal verbs) in a clear table. </p>
            </div>
        </div>
    )
}

export default Index
