import { useParams, useHistory, NavLink } from "react-router-dom"
import { useState } from "react"
import axios from "axios"
import seulverb from "./seulverb"
import Helmet from "react-helmet"



function SingleVerb() {
    const [inputValue, setinputValue] = useState("")
    function seachVerb(e) {
        e.preventDefault();
        const options = {
            method: 'GET',
            url: 'https://linguatools-conjugations.p.rapidapi.com/conjugate/',
            params: { verb: inputValue },
            headers: {
                'x-rapidapi-key': '2a50965eebmsh629d096fce14516p1319aejsn22e0ba714d98',
                'x-rapidapi-host': 'linguatools-conjugations.p.rapidapi.com'
            }
        };

        axios.request(options).then(function (response) {
            console.log(response.data);
        }).catch(function (error) {
            console.error(error);
        });

    }
    return (
        <div>
            <div className="container my-5">
                <form className="form-inline d-flex justify-content-center" onSubmit={seachVerb} >
                    <div className="form-group mb-2">
                        <input type="text" className="form-control " placeholder="Conjugation" value={inputValue} onChange={(e) => {
                            setinputValue(e.target.value)
                        }} />
                    </div>
                    <input type="submit" value="Conjugation" className="btn btn-info mb-2 ml-2" />
                </form>
                {/* <h3 className="text-danger mr-5"></h3> */}

            </div>
        </div>
    )
}

export default SingleVerb
